(1, 'Footer Links', 'footer_links', '<h4>INFORMATION</h4>\r\n<ul>\r\n<li><a href="{{store direct_url="about-magento-demo-store"}}">About Us</a></li>\r\n<li><a href="{{store direct_url="customer-service"}}">Customer Service</a></li>\r\n<li class="last privacy"><a href="{{store direct_url="privacy-policy-cookie-restriction-mode"}}">Privacy Policy</a></li>\r\n</ul>', '2015-04-14 19:46:30', '2015-04-14 20:01:24', 1),
(2, 'Footer Links Company', 'footer_links_company', '\n<div class="links">\n    <div class="block-title">\n        <strong><span>Company</span></strong>\n    </div>\n    <ul>\n        <li><a href="{{store url=""}}about-magento-demo-store/">About Us</a></li>\n        <li><a href="{{store url=""}}contacts/">Contact Us</a></li>\n        <li><a href="{{store url=""}}customer-service/">Customer Service</a></li>\n        <li><a href="{{store url=""}}privacy-policy-cookie-restriction-mode/">Privacy Policy</a></li>\n    </ul>\n</div>', '2015-04-14 19:46:34', '2015-04-14 19:46:34', 1),
(3, 'Cookie restriction notice', 'cookie_restriction_notice_block', '<p>This website requires cookies to provide all of its features. For more information on what data is contained in the cookies, please see our <a href="{{store direct_url="privacy-policy-cookie-restriction-mode"}}">Privacy Policy page</a>. To accept cookies from this site, please click the Allow button below.</p>', '2015-04-14 19:46:37', '2015-04-14 19:46:37', 1),
(4, 'footer_list_1', 'footer_list_1', '<h4>WHY BUY FROM US</h4>\r\n<ul>\r\n<li><a href="#">Shipping & Returns</a></li>\r\n<li><a href="#">Secure Shopping</a></li>\r\n<li><a href="#">International Shipping</a></li>\r\n<li><a href="#">Affiliates</a></li>\r\n<li><a href="#">Group Sales</a></li>\r\n</ul>', '2015-04-14 20:01:51', '2015-04-14 20:01:51', 1),
(5, 'footer_list_2', 'footer_list_2', '<h4>MY ACCOUNT</h4>\r\n<ul>\r\n<li><a href="{{store url=''customer/account/login/''}}">Sign In</a></li>\r\n<li><a href="{{store url=''checkout/cart/''}}">View Cart</a></li>\r\n<li><a href="{{store url=''wishlist/''}}">My Wishlist</a></li>\r\n<li><a href="#">Track My Order</a></li>\r\n<li><a href="#">Help</a></li>\r\n</ul>', '2015-04-14 20:02:19', '2015-04-14 20:02:19', 1),
(6, 'Custom tab', 'custom_tab', '<div class="tabs-custom">\r\n<span>Static CMS block displayed as additional tab.</span>\r\nYou can insert any content here (text or HTML), e.g. info about returns and refunds.</div>', '2015-04-14 20:02:58', '2015-04-14 20:02:58', 1),
(7, 'Slider', 'slider_1', '<script type="text/javascript" src="{{skin url=''js/tms-0.4.1.js''}}"> </script>\r\n<script type="text/javascript" src="{{skin url=''js/jquery.easing.1.3.js''}}"></script>\r\n<script type="text/javascript">\r\n       jQuery(window).load(function() {\r\n          jQuery(''.slider'')._TMS({\r\n			duration:800,\r\n			easing:''easeOutQuad'',\r\n			preset:''simpleFade'',\r\n			pagination:''.pagination'',\r\n		    slideshow:8000,\r\n			banners:''fade'',\r\n			waitBannerAnimation:false,\r\n			pauseOnHover:true\r\n	   		});\r\n\r\n		}); \r\n	</script>\r\n<div class="slider-bg">\r\n     <div class="slider-wrapper">\r\n        <div class="slider">\r\n            <ul class="items">\r\n                <li>\r\n                    <img src="{{skin url=''images/media/slider1.png''}}" alt=""  />\r\n                    <div class="banner">\r\n                         	<a class="link-extra" href="{{store url=''#''}}">Shop Now!</a>\r\n                    </div>\r\n                </li>\r\n                <li>\r\n                    <img src="{{skin url=''images/media/slider2.png''}}" alt=""  />\r\n                    <div class="banner">\r\n                         	<a class="link-extra" href="{{store url=''#''}}">Shop Now!</a>\r\n                    </div>\r\n                </li>\r\n                <li>\r\n                    <img src="{{skin url=''images/media/slider3.png''}}" alt=""  />\r\n                    <div class="banner">\r\n                         	<a class="link-extra" href="{{store url=''#''}}">Shop Now!</a>\r\n                    </div>\r\n                </li>\r\n            </ul>\r\n        </div> \r\n         <ul class="pagination">\r\n          <li><a href="{{store url=''#''}}">1</a></li>\r\n          <li><a href="{{store url=''#''}}">2</a></li>\r\n          <li><a href="{{store url=''#''}}">3</a></li>\r\n      </ul>\r\n    </div>\r\n	  <div class="banner-top">\r\n	      <a class="banner-block-1" href="{{store url=''#''}}"><img src="{{skin url=''images/media/banners-1.png''}}" alt=""  /></a>\r\n	      <a class="banner-block-2" href="{{store url=''#''}}"><img src="{{skin url=''images/media/banners-2.png''}}" alt=""  /></a>\r\n	      <a class="banner-block-3" href="{{store url=''#''}}"><img src="{{skin url=''images/media/banners-3.png''}}" alt=""  /></a>\r\n	  <a class="banner-block-3" href="{{store url=''#''}}"><img src="{{skin url=''images/media/banners-4.png''}}" alt=""  /></a>\r\n	  </div>\r\n</div>\r\n<div class="clear"></div>', '2015-04-14 20:03:31', '2015-04-14 20:03:31', 1),
(8, 'contact_block', 'contact_block', '<h2>Address</h2>\r\n<ul>\r\n<li><h3>Address 1:</h3>\r\n<p>The Company Name Inc. 9870 St Vincent Place,Glasgow, DC 45 </p>\r\n</li>\r\n<li><h3>Telephone:</h3>\r\n<p>+1 (234) 567-8901<br/>\r\n+1 (234) 567-8902 </p>\r\n</li>\r\n<li class="last"><h3>Address 2:</h3>\r\n<p>The Company Name Inc. 9870 St Vincent Place,Glasgow, DC 45 </p>\r\n</li>\r\n</ul>', '2015-04-14 20:04:03', '2015-04-14 20:04:03', 1),
